public class intFloatDouble {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int varEins = 10 / 3;
		float varZwei = 10f / 3f;
		double varDrei = 10d / 3d;
		
		System.out.println("int " + varEins);
		System.out.println("float " + varZwei);
		System.out.println("double " + varDrei);
	}

}
